targets.expr:
sum by (namespace, ingress, service) (
  rate(
    nginx_ingress_controller_requests{
      namespace=~"$namespace"
    }[5m]
  )
)

histogram_quantile(
  0.95,
  sum by (le, namespace, ingress, service)(
    rate(nginx_ingress_controller_request_duration_seconds_bucket{namespace=~"$namespace"}[5m])
  )
)

sum by (namespace, service) (
  kube_endpoint_address{namespace=~"$namespace", ready="true"}
)
or
sum by (namespace, service) (
  kube_service_info{namespace=~"$namespace"} * 0
)

sum by (namespace, deployment) (
  kube_deployment_status_replicas_available{namespace=~"$namespace"}
)
-
sum by (namespace, deployment) (
  kube_deployment_spec_replicas{namespace=~"$namespace"}
)

(
  sum by (namespace, deployment) (
    kube_deployment_status_replicas_ready
{
      namespace=~"$namespace"
    }
  )
)
or
on (namespace, deployment)
(
  kube_deployment_spec_replicas{
      namespace=~"$namespace"
  } * 0
)
