targets.expr:
sum by (namespace) (
  kube_pod_status_ready{
    namespace="default",
    condition="true"
  }
)

sum by (namespace, ingress, service) (
  rate(
    nginx_ingress_controller_requests{
      namespace=~"$namespace",
      service=~"$service"
    }[5m]
  )
)

histogram_quantile(
  0.95,
  sum by (le, namespace, ingress, service)(
    rate(nginx_ingress_controller_request_duration_seconds_bucket{namespace=~"$namespace", service=~"$service"}[5m])
  )
)

sum by (namespace, service) (
  kube_endpoint_address{namespace=~"$namespace", service=~"$service", ready="true"}
)
or
sum by (namespace, service) (
  kube_service_info{namespace=~"$namespace", service=~"$service"} * 0
)

(
  count by (namespace, service) (
    label_replace(
      kube_endpoint_address{
        namespace=~"$namespace",
        endpoint=~"$service",
        ready="true"
      },
      "service", "$1",
      "endpoint", "(.*)"
    )
  )
)
OR on (namespace, service)
(
  kube_service_info{
    namespace=~"$namespace",
    service=~"$service"
  } * 0
)

sum by (namespace, deployment) (
  kube_deployment_spec_replicas{namespace=~"$namespace", deployment=~"$service"}
)

sum by (namespace, deployment) (
  kube_deployment_status_replicas_available{namespace=~"$namespace", deployment=~"$service"}
)

sum by (namespace, deployment) (
  kube_deployment_status_replicas_available{namespace=~"$namespace", deployment=~"$service"}
)
-
sum by (namespace, deployment) (
  kube_deployment_spec_replicas{namespace=~"$namespace", deployment=~"$service"}
)

sum(
  kube_deployment_spec_replicas{namespace="$namespace"}
-
  kube_deployment_status_replicas_ready{namespace="$namespace"}
)

100 *
sum by (pod) (
  rate(container_cpu_usage_seconds_total{
    namespace=~"$namespace",
    pod=~"$service.*",
    container!="",
    container!="POD"
  }[$__range])
)
/
sum by (pod) (
  kube_pod_container_resource_limits{
    namespace=~"$namespace",
    pod=~"$service.*",
    resource="cpu",
    unit="core"
  }
)

100 *
sum by (pod) (
  rate(container_cpu_usage_seconds_total{
    namespace=~"$namespace",
    pod=~"^$service.*",
    container=~".+",
    container!="POD"
  }[$__range])
)
/
clamp_min(
  sum by (pod) (
    kube_pod_container_resource_limits{
      namespace=~"$namespace",
      pod=~"^$service.*",
      resource="cpu",
      unit="core"
    }
  ),
  0.001
)

100 *
sum by (pod) (
  container_memory_working_set_bytes{
    namespace=~"$namespace",
    pod=~"^$service.*",
  }
)
/
sum by (pod) (
  kube_pod_container_resource_requests{
    namespace=~"$namespace",
    pod=~"^$service.*",
    resource="memory",
    unit="byte"
  }
)

100 *
sum by (pod) (
  container_memory_working_set_bytes{
    namespace=~"$namespace",
    pod=~"^$service.*",
    container!="",
    container!="POD"
  }
)
/
sum by (pod) (
  kube_pod_container_resource_limits{
    namespace=~"$namespace",
    pod=~"^$service.*",
    resource="memory",
    unit="byte"
  }
)

(
  sum by (namespace, deployment) (
    kube_deployment_status_replicas_ready
{
      namespace=~"$namespace", deployment=~"^$service.*"
    }
  )
)
or
on (namespace, deployment)
(
  kube_deployment_spec_replicas{
      namespace=~"$namespace", deployment=~"^$service.*"
  } * 0
)

kube_pod_container_status_waiting_reason{
  namespace=~"$namespace",
  reason=~"ContainerCreating|CrashLoopBackOff|ImagePullBackOff|ErrImagePull|CreateContainerConfigError|RunContainerError"
}

kube_pod_container_info{
  namespace=~"$namespace", pod=~"^$service.*"
}
