targets.expr:
(sum(
  rate(
    nginx_ingress_controller_requests{
      status!~"[4-5].*",
      ingress=~"$ingress",
      namespace=~"$namespace"
    }[${__range_s}s]
  )
)
/
(
  sum(
    rate(
      nginx_ingress_controller_requests{
        ingress=~"$ingress",
        namespace=~"$namespace"
      }[${__range_s}s]
    )
  )
  -
  (
    sum(
      rate(
        nginx_ingress_controller_requests{
          status=~"404|499",
          ingress=~"$ingress",
          namespace=~"$namespace"
        }[${__range_s}s]
      )
    )
    or vector(0)
  )
)) * 100

histogram_quantile(
  0.99,
  sum by (le)(
    rate(
      nginx_ingress_controller_request_duration_seconds_bucket{
        status!="404|500|304|499",
        ingress=~"$ingress",
        namespace=~"$namespace"
      }[$__interval]
    )
  )
)

histogram_quantile(
  0.95,
  sum by (le)(
    rate(
      nginx_ingress_controller_request_duration_seconds_bucket{
        status!="404|500|304|499",
        ingress=~"$ingress",
        namespace=~"$namespace"
      }[$__interval]
    )
  )
)

histogram_quantile(
  0.9,
  sum by (le)(
    rate(
      nginx_ingress_controller_request_duration_seconds_bucket{
        status!="404|500|304|499",
        ingress=~"$ingress",
        namespace=~"$namespace"
      }[$__interval]
    )
  )
)

histogram_quantile(
  0.75,
  sum by (le)(
    rate(
      nginx_ingress_controller_request_duration_seconds_bucket{
        status!="404|500|304|499",
        ingress=~"$ingress",
        namespace=~"$namespace"
      }[$__interval]
    )
  )
)

histogram_quantile(
  0.5,
  sum by (le)(
    rate(
      nginx_ingress_controller_request_duration_seconds_bucket{
        status!="404|500|304|499",
        ingress=~"$ingress",
        namespace=~"$namespace"
      }[$__interval]
    )
  )
)

sum by (le)(
  increase(
    nginx_ingress_controller_request_duration_seconds_bucket{
      status!="404",status!="500",
      namespace=~"$namespace",
      ingress=~"$ingress"
    }[$__interval]
  )
)

sum(
  kube_pod_status_ready{
    namespace=~"$namespace",
    condition="true",
    pod=~"^(${ingress:regex}).*"
  }
) or vector(0)

sum(
  kube_pod_status_ready{
    namespace=~"$namespace",
    condition="false",
    pod=~"^(${ingress:regex}).*"
  }
) or vector(0)

sum(
  increase(
    kube_pod_container_status_restarts_total{
      namespace=~"$namespace",
      pod=~"^(${ingress:regex}).*"
    }[$__range]
  )
) or vector(0)

100 *
sum(
  rate(container_cpu_usage_seconds_total{
    namespace=~"$namespace",
    pod=~"$ingress.*",
    container!="",
    image!=""
  }[$__range])
)
/
sum(
  kube_pod_container_resource_limits{
    namespace=~"$namespace",
    pod=~"$ingress.*",
    resource="cpu"
  }
)

100 *
sum(
  container_memory_working_set_bytes{
    namespace=~"$namespace",
    pod=~"$ingress.*",
    container!="",
    image!=""
  }
)
/
sum(
  kube_pod_container_resource_limits{
    namespace=~"$namespace",
    pod=~"$ingress.*",
    resource="memory"
  }
)

histogram_quantile(
  0.95,
  sum by (le)(
    rate(
      nginx_ingress_controller_request_duration_seconds_bucket{
        ingress=~"$ingress",
        namespace=~"$namespace"
      }[5m]
    )
  )
)

sum by (status, method)(
  increase(
    nginx_ingress_controller_requests{
      ingress=~"$ingress",
      namespace=~"$namespace"
    }[$__range]
  )
)
